import React, { useState, useEffect } from 'react';
import Navbar2 from '../components/Navbar2';
import Footer from '../components/Footer';
import GoalCard from '../components/GoalCard';
import '../pages/styles/Goal.css';

const Goal = () => {
  const [goals, setGoals] = useState([]);
  const [newGoal, setNewGoal] = useState({
    name: '',
    targetValue: 0,
    progress: 0,
    deadline: '',
    isDraft: true,
  });

  const fetchGoals = async () => {
    try {
      const response = await fetch('http://localhost:7087/api/goals');
      const data = await response.json();
      setGoals(data);
    } catch (error) {
      console.error('Error fetching goals:', error);
    }
  };

  const handleCreateGoal = async () => {
    try {
      const response = await fetch('http://localhost:7087/api/goals', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newGoal),
      });

      if (response.ok) {
        fetchGoals();
        setNewGoal({
          name: '',
          targetValue: 0,
          progress: 0,
          deadline: '',
          isDraft: true,
        });
      } else {
        alert('Error creating goal');
      }
    } catch (error) {
      console.error('Error creating goal:', error);
    }
  };

  const handleUpdateGoal = async (goal) => {
    try {
      const response = await fetch(
        `http://localhost:7087/api/goals/${goal.id}`,
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(goal),
        }
      );

      if (response.ok) {
        fetchGoals();
      } else {
        alert('Error updating goal');
      }
    } catch (error) {
      console.error('Error updating goal:', error);
    }
  };

  const handleDeleteGoal = async (id) => {
    try {
      const response = await fetch(`http://localhost:7087/api/goals/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        fetchGoals();
      } else {
        alert('Error deleting goal');
      }
    } catch (error) {
      console.error('Error deleting goal:', error);
    }
  };

  useEffect(() => {
    fetchGoals();
  }, []);

  return (
    <div>
      <Navbar2 />
      <div className='goals-container'>
        <h1>Your Goals</h1>
        <div className='goals-grid'>
          {goals.map((goal) => (
            <GoalCard
              key={goal.id}
              goal={goal}
              onUpdate={handleUpdateGoal}
              onDelete={handleDeleteGoal}
            />
          ))}
          <div className='goal-card new-goal-card'>
            <h3>New Goal</h3>
            <input
              type='text'
              placeholder='Name'
              value={newGoal.name}
              onChange={(e) => setNewGoal({ ...newGoal, name: e.target.value })}
            />
            <input
              type='number'
              placeholder='Target Value'
              value={newGoal.targetValue}
              onChange={(e) =>
                setNewGoal({ ...newGoal, targetValue: e.target.value })
              }
            />
            <input
              type='number'
              placeholder='Progress'
              value={newGoal.progress}
              onChange={(e) =>
                setNewGoal({ ...newGoal, progress: e.target.value })
              }
            />
            <input
              type='date'
              value={newGoal.deadline}
              onChange={(e) =>
                setNewGoal({ ...newGoal, deadline: e.target.value })
              }
            />
            <label>
              <input
                type='checkbox'
                checked={newGoal.isDraft}
                onChange={(e) =>
                  setNewGoal({ ...newGoal, isDraft: e.target.checked })
                }
              />
              Is Draft
            </label>
            <button onClick={handleCreateGoal}>Add Goal</button>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Goal;
