import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form } from 'react-bootstrap';
import axios from 'axios';
import '../pages/styles/Goal.css'; // Stiluri avansate pentru componentă

const Goal = () => {
  const [objectives, setObjectives] = useState([]);
  const [drafts, setDrafts] = useState([]); // Adaugă starea pentru drafturi
  const [discounts, setDiscounts] = useState([]); // Adaugă starea pentru discounturi
  const [showModal, setShowModal] = useState(false);
  const [currentObjective, setCurrentObjective] = useState({
    objectiveType: '',
    targetValue: 0,
    startDate: '',
    deadline: '',
    progress: 0,
  });
  const [popupMessage, setPopupMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const UserId = 1; // Sau preia din context sau din autentificare

  // Fetch objectives
  const fetchObjectives = async () => {
    try {
      const response = await axios.get('https://localhost:7087/api/objective'); // Endpoint corect
      const allObjectives = response.data;

      // Separa drafturile de obiectivele finalizate
      const drafts = allObjectives.filter((obj) => obj.isDraft);
      const finalizedObjectives = allObjectives.filter((obj) => !obj.isDraft);

      setObjectives(finalizedObjectives); // Salvează obiectivele finalizate
      setDrafts(drafts); // Salvează drafturile
    } catch (error) {
      console.error('Error fetching objectives', error);
    }
  };

  // Fetch discounts
  const fetchDiscounts = async () => {
    try {
      const response = await axios.get('https://localhost:7087/api/discount'); // Endpoint corect
      setDiscounts(response.data);
    } catch (error) {
      console.error('Error fetching discounts', error);
    }
  };

  useEffect(() => {
    fetchObjectives();
    fetchDiscounts();
  }, []);

  // Handle Save Draft
  const handleSaveDraft = async () => {
    try {
      const response = await axios.post(
        'https://localhost:7087/api/objective/save-draft', // Endpoint corect
        {
          userId: UserId,
          objectiveType: currentObjective.objectiveType,
          targetValue: currentObjective.targetValue,
          startDate: currentObjective.startDate,
          deadline: currentObjective.deadline,
          progress: currentObjective.progress,
        }
      );
      alert('Draft saved successfully!');
      fetchObjectives(); // Actualizează lista cu obiective
      setShowModal(false); // Închide modal-ul
    } catch (error) {
      console.error('Error saving draft:', error);
      alert('Failed to save draft.');
    }
  };

  // Handle Edit Draft
  const handleEditDraft = (draft) => {
    setCurrentObjective({
      objectiveType: draft.objectiveType,
      targetValue: draft.targetValue,
      startDate: draft.startDate,
      deadline: draft.deadline,
      progress: draft.progress,
    });
    setShowModal(true); // Deschide modal-ul pentru editare
  };

  // Handle Save Final (când obiectivul este complet)
  const handleSaveFinal = async (id) => {
    try {
      const response = await axios.post(
        `https://localhost:7087/api/objective/save-final?userId=${UserId}&objectiveId=${id}`
      );
      setPopupMessage(response.data.message || 'Objective saved successfully!');
      setShowPopup(true);
      fetchObjectives(); // Actualizează obiectivele
      fetchDiscounts(); // Actualizează discount-urile
    } catch (error) {
      setPopupMessage(error.response?.data || 'Error saving objective.');
      setShowPopup(true);
    }
  };

  // Modal pentru adăugare/editar obiectiv
  const handleCloseModal = () => {
    setShowModal(false);
    setCurrentObjective({
      objectiveType: '',
      targetValue: 0,
      startDate: '',
      deadline: '',
      progress: 0,
    });
  };

  const handleShowModal = () => setShowModal(true);

  return (
    <div className='goal-manager'>
      <h2>Manage Your Objectives</h2>
      <Button variant='primary' onClick={handleShowModal}>
        Add New Objective
      </Button>

      {/* Tabel pentru obiective finalizate */}
      <h2>Your Objectives</h2>
      <Table striped bordered hover className='objectives-table'>
        <thead>
          <tr>
            <th>Type</th>
            <th>Target Value</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Progress</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {objectives.map((obj) => (
            <tr key={obj.objectiveId}>
              <td>{obj.objectiveType}</td>
              <td>{obj.targetValue}</td>
              <td>{new Date(obj.startDate).toLocaleDateString()}</td>
              <td>{new Date(obj.deadline).toLocaleDateString()}</td>
              <td>{obj.progress}</td>
              <td>
                <Button
                  variant='secondary'
                  onClick={() => handleSaveFinal(obj.objectiveId)}
                  disabled={!obj.isDraft}
                >
                  Save Final
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Tabel pentru drafturi */}
      <h2>Your Drafts</h2>
      <Table striped bordered hover className='drafts-table'>
        <thead>
          <tr>
            <th>Type</th>
            <th>Target Value</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Progress</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {drafts.map((draft) => (
            <tr key={draft.objectiveId}>
              <td>{draft.objectiveType}</td>
              <td>{draft.targetValue}</td>
              <td>{new Date(draft.startDate).toLocaleDateString()}</td>
              <td>{new Date(draft.deadline).toLocaleDateString()}</td>
              <td>{draft.progress}</td>
              <td>
                <Button
                  variant='secondary'
                  onClick={() => handleEditDraft(draft)} // Funcție pentru a edita draftul
                >
                  Edit Draft
                </Button>
                <Button
                  variant='success'
                  onClick={() => handleSaveFinal(draft.objectiveId)} // Funcție pentru a salva obiectivul final
                  disabled={!draft.isDraft}
                >
                  Save Final
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Tabel pentru discounturi */}
      <h2>Your Discounts</h2>
      <Table striped bordered hover className='discounts-table'>
        <thead>
          <tr>
            <th>Discount Percent</th>
            <th>Grant Date</th>
            <th>Expiration Date</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {discounts.map((discount) => (
            <tr key={discount.discountId}>
              <td>{discount.discountPercent}%</td>
              <td>{new Date(discount.grantDate).toLocaleDateString()}</td>
              <td>{new Date(discount.expirationDate).toLocaleDateString()}</td>
              <td>{discount.isUsed ? 'Used' : 'Active'}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal pentru adăugare obiectiv */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add/Edit Objective</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Objective Type</Form.Label>
              <Form.Control
                type='text'
                value={currentObjective.objectiveType}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    objectiveType: e.target.value,
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Target Value</Form.Label>
              <Form.Control
                type='number'
                value={currentObjective.targetValue}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    targetValue: parseInt(e.target.value),
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type='date'
                value={currentObjective.startDate}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    startDate: e.target.value,
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type='date'
                value={currentObjective.deadline}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    deadline: e.target.value,
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Progress</Form.Label>
              <Form.Control
                type='number'
                value={currentObjective.progress}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    progress: parseInt(e.target.value),
                  })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant='primary' onClick={handleSaveDraft}>
            Save Draft
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Pop-up de feedback */}
      {showPopup && (
        <div className='popup-message'>
          <p>{popupMessage}</p>
          <Button onClick={() => setShowPopup(false)}>Close</Button>
        </div>
      )}
    </div>
  );
};

export default Goal;
