import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getWorkouts } from '../services/WorkoutService';

const WorkoutDetails = () => {
  const { id } = useParams(); // Preluăm workoutId din URL
  const [workout, setWorkout] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchWorkoutDetails = async () => {
      const data = await getWorkouts();
      const workoutDetail = data.find(
        (workout) => workout.workoutId === parseInt(id)
      );

      if (workoutDetail) {
        setWorkout(workoutDetail);
      } else {
        console.error('Workout not found for ID:', id); // Logăm eroarea în caz că nu găsim workout-ul
      }
      setIsLoading(false);
    };

    fetchWorkoutDetails();
  }, [id]);

  if (isLoading) {
    return <div>Loading workout details...</div>;
  }

  if (!workout) {
    return <div>Workout not found.</div>; // Mesaj în cazul în care workout-ul nu există
  }

  return (
    <div className='workout-details-container'>
      <h2>{workout.workoutDescription}</h2>
      <p>Type: {workout.workoutType}</p>
      <p>Difficulty: {workout.difficultyLevel}</p>
      <p>Duration: {workout.workoutDuration}</p>
      <p>Calories burned: {workout.caloriesBurned}</p>

      {/* Afișăm conținutul - poze sau video-uri */}
      <div className='workout-content'>
        <h3>Content</h3>
        <p>Check out the workout tutorial below:</p>
        <iframe
          width='560'
          height='315'
          src={workout.contentPath}
          title={workout.workoutDescription}
          frameBorder='0'
          allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture'
          allowFullScreen
        ></iframe>
      </div>
    </div>
  );
};

export default WorkoutDetails;
