import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form } from 'react-bootstrap';
import axios from 'axios';
import '../pages/styles/Goal.css'; // Stiluri avansate pentru componentă
import 'bootstrap/dist/css/bootstrap.min.css';

const Goal = () => {
  const [objectives, setObjectives] = useState([]); // Obiective finalizate
  const [drafts, setDrafts] = useState([]); // Drafturi de obiective
  const [discounts, setDiscounts] = useState([]); // Discounturi
  const [workouts, setWorkouts] = useState([]); // Workout-uri completate
  const [showModal, setShowModal] = useState(false);
  const [currentObjective, setCurrentObjective] = useState({
    objectiveId: null,
    objectiveType: '',
    targetValue: 0,
    startDate: '',
    deadline: '',
    progress: 0,
  });
  const [popupMessage, setPopupMessage] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [newWorkout, setNewWorkout] = useState({
    type: '',
    duration: '',
    date: new Date().toISOString().split('T')[0],
  });

  const UserId = 1; // Sau preia din context sau din autentificare

  // Fetch objectives
  const fetchObjectives = async () => {
    try {
      const response = await axios.get('https://localhost:7087/api/objective');
      const allObjectives = response.data;

      const drafts = allObjectives.filter((obj) => obj.isDraft);
      const finalizedObjectives = allObjectives.filter((obj) => !obj.isDraft);

      setDrafts(drafts); // Setează drafturile
      setObjectives(finalizedObjectives); // Setează obiectivele finale
    } catch (error) {
      console.error('Error fetching objectives:', error);
    }
  };

  // Fetch discounts
  const fetchDiscounts = async () => {
    try {
      const response = await axios.get('https://localhost:7087/api/discount');
      setDiscounts(response.data);
    } catch (error) {
      console.error('Error fetching discounts', error);
    }
  };

  // Fetch workouts
  const fetchWorkouts = async () => {
    try {
      const response = await axios.get(
        `https://localhost:7087/api/workout?userId=${UserId}`
      );
      setWorkouts(response.data);
    } catch (error) {
      console.error('Error fetching workouts', error);
    }
  };

  useEffect(() => {
    fetchObjectives();
    fetchDiscounts();
    fetchWorkouts(); // Fetch workout-uri
  }, []);

  // Handle Save Draft (actualizează draftul existent dacă are un id)
  const handleSaveDraft = async () => {
    try {
      const response = await axios.post(
        'https://localhost:7087/api/objective/save-draft',
        {
          objectiveId: currentObjective.objectiveId || 0, // 0 înseamnă obiectiv nou
          objectiveType: currentObjective.objectiveType,
          targetValue: currentObjective.targetValue,
          startDate: currentObjective.startDate,
          deadline: currentObjective.deadline,
          progress: currentObjective.progress,
          userId: UserId, // Dacă este necesar
        }
      );

      alert('Draft saved successfully!');
      fetchObjectives(); // Actualizează datele
      setShowModal(false);
    } catch (error) {
      console.error('Error saving draft:', error);
      alert('Failed to save draft.');
    }
  };

  // Handle Add Workout
  const handleAddWorkout = async () => {
    try {
      const response = await axios.post('https://localhost:7087/api/workout', {
        userId: UserId,
        type: newWorkout.type,
        duration: newWorkout.duration,
        date: newWorkout.date,
      });

      setWorkouts([...workouts, response.data]); // Adaugă workout-ul în stare
      alert('Workout added successfully!');
    } catch (error) {
      console.error('Error adding workout:', error);
      alert('Failed to add workout.');
    }
  };

  // Check eligibility for discount
  const checkEligibilityForDiscount = (objective) => {
    const eligibleWorkouts = workouts.filter(
      (workout) =>
        new Date(workout.date) >= new Date(objective.startDate) &&
        new Date(workout.date) <= new Date(objective.deadline)
    );

    if (
      eligibleWorkouts.length >= 5 &&
      objective.progress >= objective.targetValue
    ) {
      alert('You are eligible for a 10% discount!');
    } else {
      alert('You are not eligible for a discount yet.');
    }
  };

  // Handle Save Final (când obiectivul este complet)
  const handleSaveFinal = async (id) => {
    console.log('Saving final objective with ID:', id); // Log pentru debugging
    if (!id) {
      alert('Invalid objective ID');
      return;
    }

    try {
      const response = await axios.post(
        `https://localhost:7087/api/objective/save-final?userId=${UserId}&objectiveId=${id}`
      );

      if (response.status === 200) {
        alert('Objective saved successfully!');
        fetchObjectives(); // Actualizează obiectivele
        fetchDiscounts(); // Reîncarcă discounturile
      } else {
        alert('Error saving the objective.');
      }
    } catch (error) {
      console.error('Error saving objective:', error);
      alert(error.response?.data?.message || 'Error saving objective.');
    }
  };

  return (
    <div className='goal-manager'>
      <h2>Manage Your Objectives</h2>
      <Button variant='primary' onClick={() => setShowModal(true)}>
        Add New Objective
      </Button>

      {/* Tabel pentru obiective finalizate */}
      <h2>Your Objectives</h2>
      <Table striped bordered hover className='objectives-table'>
        <thead>
          <tr>
            <th>Type</th>
            <th>Target Value</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Progress</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {objectives.map((obj) => (
            <tr key={obj.objectiveId}>
              <td>{obj.objectiveType}</td>
              <td>{obj.targetValue}</td>
              <td>{new Date(obj.startDate).toLocaleDateString()}</td>
              <td>{new Date(obj.deadline).toLocaleDateString()}</td>
              <td>{obj.progress}</td>
              <td>
                <Button
                  variant='secondary'
                  onClick={() => handleSaveFinal(obj.objectiveId)}
                  disabled={!obj.isDraft}
                >
                  Save Final
                </Button>
                <Button
                  variant='info'
                  onClick={() => checkEligibilityForDiscount(obj)}
                >
                  Check Discount Eligibility
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Tabel pentru workout-uri */}
      <h2>Your Workouts</h2>
      <Table striped bordered hover className='workouts-table'>
        <thead>
          <tr>
            <th>Date</th>
            <th>Workout Type</th>
            <th>Duration</th>
          </tr>
        </thead>
        <tbody>
          {workouts.map((workout) => (
            <tr key={workout.id}>
              <td>{new Date(workout.date).toLocaleDateString()}</td>
              <td>{workout.type}</td>
              <td>{workout.duration} minutes</td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Formular pentru adăugarea unui workout */}
      <h2>Add a New Workout</h2>
      <Form>
        <Form.Group>
          <Form.Label>Workout Type</Form.Label>
          <Form.Control
            type='text'
            value={newWorkout.type}
            onChange={(e) =>
              setNewWorkout({ ...newWorkout, type: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Duration (minutes)</Form.Label>
          <Form.Control
            type='number'
            value={newWorkout.duration}
            onChange={(e) =>
              setNewWorkout({ ...newWorkout, duration: e.target.value })
            }
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Date</Form.Label>
          <Form.Control
            type='date'
            value={newWorkout.date}
            onChange={(e) =>
              setNewWorkout({ ...newWorkout, date: e.target.value })
            }
          />
        </Form.Group>
        <Button variant='primary' onClick={handleAddWorkout}>
          Add Workout
        </Button>
      </Form>

      {/* Modal pentru adăugare/editar obiectiv */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add/Edit Objective</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Objective Type</Form.Label>
              <Form.Control
                type='text'
                value={currentObjective.objectiveType}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    objectiveType: e.target.value,
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Target Value</Form.Label>
              <Form.Control
                type='number'
                value={currentObjective.targetValue}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    targetValue: parseInt(e.target.value),
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type='date'
                value={currentObjective.startDate}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    startDate: e.target.value,
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type='date'
                value={currentObjective.deadline}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    deadline: e.target.value,
                  })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Progress</Form.Label>
              <Form.Control
                type='number'
                value={currentObjective.progress}
                onChange={(e) =>
                  setCurrentObjective({
                    ...currentObjective,
                    progress: parseInt(e.target.value),
                  })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' onClick={() => setShowModal(false)}>
            Close
          </Button>
          <Button variant='primary' onClick={handleSaveDraft}>
            Save Draft
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Goal;
