import React from 'react';

const GoalCard = ({ goal, onUpdate, onDelete }) => {
  const handleToggleDraft = () => {
    onUpdate({ ...goal, isDraft: !goal.isDraft });
  };

  return (
    <div className='goal-card'>
      <h3>{goal.name}</h3>
      <p>
        <strong>Target Value:</strong> {goal.targetValue}
      </p>
      <p>
        <strong>Progress:</strong> {goal.progress}
      </p>
      <p>
        <strong>Deadline:</strong> {goal.deadline}
      </p>
      <p>
        <strong>Status:</strong> {goal.isDraft ? 'Draft' : 'Finalized'}
      </p>
      <div className='goal-card-actions'>
        <button onClick={handleToggleDraft}>
          {goal.isDraft ? 'Finalize' : 'Mark as Draft'}
        </button>
        <button onClick={() => onDelete(goal.id)}>Delete</button>
      </div>
    </div>
  );
};

export default GoalCard;
